package tipodeDato;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		System.out.println("Mininmo valor de un Integer:"+ Integer.MIN_VALUE);
		System.out.println("Mayor valor de un Integer:"+ Integer.MAX_VALUE);
		System.out.println("Numero de bit de un Integer:"+ Integer.SIZE);
		System.out.println("Numero de bytes de un Integer:"+ Integer.BYTES);
		
		System.out.println("Mayor valor de un Long:"+ Long.MAX_VALUE);
		
		
		//Mostrar los atributos de la clase Integer.parseInt();
		
		System.out.println(Integer.parseInt("0",10));
		System.out.println(Integer.parseInt("473",10));
		System.out.println(Integer.parseInt("+42",10));
		System.out.println(Integer.parseInt("FF",16));

	}

}

